mapper.insert:
select last_insert_id() as id
insert into privilege_resource (
) values ( #{name, jdbcType=VARCHAR}, #{code, jdbcType=VARCHAR}, #{status, jdbcType=INTEGER}, #{createTime, jdbcType=TIMESTAMP}
)

mapper.select:
 select * from  USER_SERIAL_RECORD where 1=1
 
    and APP_ID=#{appId} 
 
 
    and PAY_TYPE=#{payType} 
 
 
    and SERIAL_NO=#{serialNo} 
 
 
    and USER_NAME=#{userName} 
 
 
    and CREATE_TIME BETWEEN #{startDate, jdbcType=TIMESTAMP} 
 
 
    and #{endDate, jdbcType=TIMESTAMP}
 
limit #{startRow},#{pageSize}

select count(*) from  USER_SERIAL_RECORD where 1=1

   and APP_ID=#{appId} 


   and PAY_TYPE=#{payType} 


   and SERIAL_NO=#{serialNo} 


   and USER_NAME=#{userName} 


   and CREATE_TIME BETWEEN #{startDate, jdbcType=TIMESTAMP} 


   and #{endDate, jdbcType=TIMESTAMP}
